import { Sidebar } from "./Sidebar";
import { Table } from "../../components/Table";
import { useEffect, useState } from "react";
import axios from "axios";

interface User {
    id: string;
    name: string;
    email: string;
    role: string;
}

const Admin = () => {
  const [allUser, setAllUser] = useState<User[]>();
  const [showMore, setShowMore] = useState<string>("");

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("/api/user/getAllUser");
      setAllUser(response.data.allUser);
    };
    fetchData();
  }, []);

  const showMoreHandler = (id: string) => {
    setShowMore(id);
  };

  return (
    <div className="flex flex-no-wrap " >
      {/* Sidebar starts */}
      {/* Remove class [ hidden ] and replace [ sm:flex ] with [ flex ] */}
      <div className="z-50">
        <Sidebar />
      </div>
      {/* Sidebar ends */}

      {/* Remove class [ h-64 ] when adding a card block */}
      <div className="py-5 md:w-4/5 w-11/12 ">
        {/* Remove class [ border-dashed border-2 border-gray-300 ] to remove dotted border */}
        <Table allUser={allUser} showMore={showMore} showMoreHandler={showMoreHandler}/>
      </div>
    </div>
  );
};

export default Admin;
